import { useEffect, useState } from 'react';
import Styles from './TODO.module.css';
import { dummy } from './dummy';
import axios from 'axios';

export function TODO(props) {

  const [newTodo, setNewTodo] = useState('');
  const [todoData, setTodoData] = useState(dummy);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // State to store errors
  const [editingTodoId, setEditingTodoId] = useState(null); // Keeps track of editing todo

  useEffect(() => {
    const fetchTodo = async () => {
      try {
        const apiData = await getTodo();
        setTodoData(apiData);
      } catch (err) {
        setError(err.message || 'Failed to load todos'); // Set error message
      } finally {
        setLoading(false);
      }
    };
    fetchTodo();
  }, []);

  const getTodo = async () => {
    const options = {
      method: "GET",
      url: `http://localhost:8000/api/todo`,
      headers: {
        accept: "application/json",
      }
    };
    const response = await axios.request(options);
    return response.data;
  };

  const addTodo = async () => {
    if (!newTodo) return; // Prevent adding empty todos

    const options = {
      method: "POST",
      url: `http://localhost:8000/api/todo`,
      headers: {
        accept: "application/json",
      },
      data: {
        title: newTodo,
      }
    };
    try {
      const response = await axios.request(options);
      console.log(response.data);
      setTodoData(prevData => [...prevData, response.data.newTodo]);
      setNewTodo('');
    } catch (err) {
      setError(err.message || 'Failed to add todo');
    }
  };

  const deleteTodo = async (id) => {
    const options = {
      method: "DELETE",
      url: `http://localhost:8000/api/todo/${id}`,
      headers: {
        accept: "application/json",
      }
    };
    try {
      const response = await axios.request(options);
      console.log(response.data);
      setTodoData(prevData => prevData.filter(todo => todo._id !== id));
    } catch (err) {
      setError(err.message || 'Failed to delete todo');
    }
  };

  const updateTodo = async (id, title) => {
    const options = {
      method: "PATCH",
      url: `http://localhost:8000/api/todo/${id}`,
      headers: {
        accept: "application/json",
      },
      data: {
        title,
      }
    };
    try {
      const response = await axios.request(options);
      console.log(response.data);
      setTodoData(prevData => prevData.map(todo => todo._id === id ? response.data : todo));
    } catch (err) {
      setError(err.message || 'Failed to update todo');
    }
  };

  const handleEditClick = (id) => {
    setEditingTodoId(id); // Set editing state for the todo
  };

  const handleEditSave = async (id, title) => {
    if (!title) return; // Prevent saving empty titles
    await updateTodo(id, title);
    setEditingTodoId(null); // Clear editing state
  };

  return (
    <div className={Styles.ancestorContainer}>
      <div className={Styles.headerContainer}>
        <h1>
          Tasks
        </h1>
        <span>
          <input
            className={Styles.todoInput}
            type='text'
            name='New Todo'
            value={newTodo}
            onChange={(event) => {
              setNewTodo(event.target.value);
            }}
          />
          <button
            id='addButton'
            name='add'
            className={Styles.addButton}
            
            onClick={addTodo}
          >
            + New Todo
          </button>
        </span>
      </div>
      <div id='todoContainer' className={Styles.todoContainer}>
        {loading ? (
          <p>Loading tasks...</p>
        ) : error ? (
          <p style={{ color: 'red' }}>Error: {error}</p>
        ) : todoData.length > 0 ? (
          todoData.map((entry, index) => (
            <div key={entry._id} className={Styles.todo}>
              {editingTodoId === entry._id ? (
                <input
                  type='text'
                  value={entry.title} // Pre-fill with current title
                  onChange={(event) => handleEditSave(entry._id, event.target.value)}
                />
              ) : (
                <span className={Styles.infoContainer}>
                  <input
                    type='checkbox'
                    checked={entry.done}
                    onChange={() => updateTodo(entry._id)}
                  />
                  {entry.title}
                </span>
              )}
              <span style={{ cursor: 'pointer' }} onClick={() => deleteTodo(entry._id)}>
                delete
              </span>
            </div>
          ))
        ) : (
          <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
        )}
      </div>
    </div>
  );
}
